import { Component, HostListener, OnInit } from '@angular/core';
import { UserService } from '../../services/user.service';

@Component({
  selector: 'app-profile',
  standalone: false,
  templateUrl: './profile.component.html',
  styleUrl: './profile.component.css',
})
export class ProfileComponent implements OnInit {
  newList: boolean = false;
  shareList: boolean = false;
  constructor(private user: UserService) { }

  recentViewItem: any;
  wishListItem: any;
  @HostListener('window:resize')
  emptyWishList = false;
  emptyRecentView = false;
  selectedProduct:any;
  sharewishtList:any;

  products = [
    { name: 'Black Tee', price: 100, image: 'data:image/jpeg;base64,', color: 'Black' },
    { name: 'Gray Tee', price: 80, image: 'data:image/jpeg;base64,', color: 'Gray' },
    { name: 'White Tee', price: 120, image: 'assets/images/white-tshirt.png', color: 'White' },
    { name: 'Blue Tee', price: 110, image: 'assets/images/blue-tshirt.png', color: 'Blue' },
    { name: 'Red Tee', price: 130, image: 'assets/images/red-tshirt.png', color: 'Red' },
    { name: 'Green Tee', price: 140, image: 'data:image/jpeg;base64,', color: 'Green' },
  ];

  visibleProducts: any[any] = [];
  startIndex = 0;
  visibleCount = 4;
  addNewProduct = {
  customer_id: 8,
  name: '',
  is_public: false
};


  ngOnInit(): void {
  
    this.getRecentViewe();
    this.getWishListData();

  }



  get limitedWishlist() {
    return this.wishListItem.data?.slice(0, 4) || [];
  }
  getFirstThreeImages(images: any[]): any[] {
    if (!images || !Array.isArray(images)) {
      return [];
    }
    return images.slice(0, 3);
  }


  getWishListData() {
    this.user.getWishList().subscribe((res: any) => {
      this.wishListItem = res.data;
      console.log(this.wishListItem, "wishListData")
    })
  }

  getRecentViewe() {
    this.user.getRecetView().subscribe((res: any) => {
      this.recentViewItem = res.data.items;
      // this.recentViewItem = (this.recentViewItem as any[]).filter(x=> x.product_details != null);
      console.log(this.recentViewItem,"recentView");

      if (this.recentViewItem) {
        this.updateVisibleCount();
        this.updateVisibleProducts();
      }

    })
  }

  updateVisibleCount() {
    const width = window.innerWidth;

    if (width < 768) {
      this.visibleCount = 1; // mobile
    } else if (width < 1200) {
      this.visibleCount = 2; // tablet
    } else {
      this.visibleCount = 4; // laptop/desktop
    }
  }




  updateVisibleProducts() {
    this.visibleProducts = this.recentViewItem.slice(this.startIndex, this.startIndex + this.visibleCount);
    console.log(this.visibleProducts, "visible")
  }

  next() {
    if (this.startIndex + this.visibleCount < this.recentViewItem.length) {
      this.startIndex += 1;
    } else {
      this.startIndex = 0; // loop back to start
    }
    this.updateVisibleProducts();
  }

  prev() {
    if (this.startIndex > 0) {
      this.startIndex -= 1;
    } else {
      this.startIndex = this.recentViewItem.length - this.visibleCount;
    }
    this.updateVisibleProducts();
  }

  nextWL() {
  const container = document.querySelector('.wishlist-wrapper') as HTMLElement;
  container.scrollLeft -= 300;
  }

  prevWL() { 
  const container = document.querySelector('.wishlist-wrapper') as HTMLElement;
  container.scrollLeft += 300;

  }

    editProduct(product: any) {
    // Clone product to avoid directly modifying the list before saving
    this.selectedProduct = { ...product };
    console.log(product,"wishlist Edit")
    
  }

    saveWishlist() {
    if (!this.selectedProduct) return;

    const body = {
      customer_id: this.selectedProduct.customer_id,
      name: this.selectedProduct.name,
      is_public: this.selectedProduct.is_public
    };

    this.user.updateWishlist(this.selectedProduct.id, body).subscribe({
      next: (res) => {
        // alert('Wishlist updated successfully!');
        // this.selectedProduct = null; // reset
        this.getWishListData(); // reload data
      },
      error: (err) => {
        console.error('Update failed:', err);
        alert('Failed to update wishlist.');
      }
    });
  }

  saveNewWishlist(){
    this.user.newWishlist(this.addNewProduct).subscribe((res:any)=>{
      if(res){
        this.getWishListData();
      }else{
       alert('Failed to add wishlist.');
      }
      console.log(res);
    })
  }

  addNewList(){
    this.newList = true;
    console.log(this.newList)
  }
  shareListAll(product:any){
    this.selectedProduct = { ...product };
    this.shareList = true;
  }
  modelOff(){
    this.newList =false;
    this.shareList = false;
    console.log(this.newList,this.shareList,"lm");
    
  }

copyLink(link: string) {
  navigator.clipboard.writeText(link).then(() => {
    // this.copied = true;
    // setTimeout(() => this.copied = false, 2000);
  });

  }
}
